import axios from "axios";

const api = 'http://localhost:5000'

export const login = async (email, password) => {
    try {
        const formdata = new FormData();
        formdata.append("email", email);
        formdata.append("password", password);
        return await axios.post(`${api}/api/user/login`, formdata);
    } 
    catch (err) {
        return -1;
    }
}

export const register = async (name, businessName, password, address, phoneNo, email) => {
    try {
        const formdata = new FormData();
        formdata.append("name", name);
        formdata.append("businessName", businessName);
        formdata.append("password", password);
        formdata.append("address", address);
        formdata.append("phoneNo", phoneNo);
        formdata.append("email", email);
        return await axios.post('http://localhost:5000/api/user/registration', formdata);
    }
    catch (err) {
        return -1;
    }
}

export const logout = async () => {
    try {
        localStorage.removeItem("token");
        localStorage.removeItem("userId");
        localStorage.removeItem("userEmail");
        localStorage.removeItem("userName");
        localStorage.removeItem("userPhone");
        localStorage.removeItem("userType");
        localStorage.removeItem("userJoinDate");
        return await axios.post('http://localhost:5000/api/user/logout');
    }
    catch(err){
        return -1;
    }
}

export const isLoggedIn = async () => {
    try {
        return await axios.get('http://localhost:5000/api/user/isLoggedIn')
    }
    catch (err){
        return -1;
    }
}
