import React, {useState, useEffect} from 'react';

import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Grid from '@mui/material/Grid';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import DatePicker from '@mui/lab/DatePicker';

import style from "../../styles/seasonsStyles"
import useInputState from '../../hooks/useInputState';
import * as adminApi from '../../api/admin'

const Alert = React.forwardRef(function Alert(props, ref) {
    return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});

const minDate = new Date('2022-01-01T00:00:00.000');
const maxDate = new Date('2100-01-01T00:00:00.000');

function Season(){
    const classes = style();
    const [openAddForm, setOpenAddForm] = useState(false);
    const [seasonName, handleChangeSeasonName, setSeasonName] = useInputState("");
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [snakeBarOpen, setSnakeBarOpen] = useState(false);
    const [snakeBarType, setSnakeBarType] = useState("success");
    const [snakeMessage, setSnakeMessage] = useState("");

    const handleCloseSnakeBar = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setSnakeBarOpen(false);
    };
    
    const openForm = () => {
        setOpenAddForm(true);
    }

    const cancelSeason = () => {
        setOpenAddForm(false);
    }

    const submitForm = async (e) => {
        e.preventDefault(); 
        let response = await adminApi.addSeason(seasonName, startDate.setDate(), startDate.getMonth(), endDate.getDate(), endDate.getMonth());
        if(response.status === 200){
            setSnakeBarOpen(true);
            setSnakeBarType('success');
            setSnakeMessage("season")
        }
    }

    return(
        <div>
            <Snackbar open={snakeBarOpen} autoHideDuration={6000} onClose={handleCloseSnakeBar}>
                <Alert onClose={handleCloseSnakeBar} severity={snakeBarType} sx={{ width: '100%' }}>
                    {snakeMessage}
                </Alert>
            </Snackbar>
            <Box sx={{ width: '100%' }}>
                <Typography variant="h3" component="div" gutterBottom className={classes.heading}>
                    Add a new season
                </Typography>
                {openAddForm === false ? (
                    <Grid container justifyContent="center" alignItems="center">
                        <Button variant="contained" 
                         className={classes.addButton}
                            className={classes.placeButton}
                            onClick={openForm}
                            disabled={openAddForm}
                            sx={{width:"25%", minWidth: "270px"}}
                        >
                            Add a season
                        </Button>
                    </Grid>
                ) : (
                    <form>
                        <Box sx={{ width: '100%' }}>
                            <Grid container item direction="column" spacing={2} justifyContent="center" alignItems="center">
                                <Grid item>
                                    <TextField id="registration-name" 
                                        label="Season Name" 
                                        variant="standard" 
                                        value={seasonName} 
                                        onChange={handleChangeSeasonName}
                                        required
                                        sx={{width:"50%", minWidth: "270px", marginBottom: "1rem"}}
                                    />
                                </Grid>
                                    <Grid item> 
                                        <LocalizationProvider dateAdapter={AdapterDateFns}>
                                            <DatePicker
                                                openTo="year"
                                                views={['month', 'day']}
                                                label="Select when the season starts"
                                                value={startDate}
                                                onChange={(newValue) => {
                                                    setStartDate(newValue);
                                                }}
                                                renderInput={(params) => <TextField {...params} helperText={null} />}
                                            />
                                        </LocalizationProvider>
                                    </Grid>
                                    <Grid item> 
                                        <LocalizationProvider dateAdapter={AdapterDateFns}>
                                        <DatePicker
                                            openTo="year"
                                            views={['month', 'day']}
                                            label="Select when the season Ends"
                                            value={endDate}
                                            onChange={(newValue) => {
                                                setEndDate(newValue);
                                            }}
                                            renderInput={(params) => <TextField {...params} helperText={null} />}
                                        />
                                        </LocalizationProvider>
                                    </Grid>
                                <Grid item>
                                    <Button fullWidth onClick={submitForm} variant="contained" sx={{width:"50%", minWidth: "270px"}}>Add</Button>
                                </Grid>
                                <Grid item>
                                    <Button fullWidth onClick={cancelSeason} variant="contained" sx={{width:"50%", minWidth: "270px"}}>Cancel</Button>
                                </Grid>
                            </Grid>
                        </Box>
                    </form>
                )}
            </Box>
        </div>
    )
}

export default Season;
