import React from 'react'

// MaterialUI Elements
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

import * as adminApi from "../../../api/admin"

export default function SeasonsItems(props) {
    const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

    const {
        SEASON_DESCRIPTION,
        SEASON_END_DAY,
        SEASON_END_MONTH,
        SEASON_NAME,
        SEASON_START_DAY,
        SEASON_START_MONTH,
    } = props

    '2022-01-01T00:00:00.000'
    if(SEASON_START_MONTH < 9){
        SEASON_START_MONTH = `0{}`
    }
    let startDateString = `2022-${SEASON_START_MONTH}-${SEASON_START_DAY}T00:00:00.000`

    const [openAddForm, setOpenAddForm] = useState(false);
    const [seasonName, handleChangeSeasonName] = useInputState(SEASON_NAME);
    const [description, handleChangeDescription] = useInputState(SEASON_DESCRIPTION);
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [snakeBarOpen, setSnakeBarOpen] = useState(false);
    const [snakeBarType, setSnakeBarType] = useState("success");
    const [snakeMessage, setSnakeMessage] = useState("");

    console.log(props)

    return (
        <React.Fragment>

            <Dialog open={openAddForm} onClose={cancelSeason}>
                    <form>
                        <DialogTitle>Add Season</DialogTitle>
                        <DialogContent>
                            <DialogContentText>
                                To add a new season, enter the name of the season and select the start and end date of the season. year dosen't matter, select the month and date.
                            </DialogContentText>
                            <Box sx={{ width: '100%' }}>
                                <Grid container 
                                    item 
                                    direction="column" 
                                    spacing={2} 
                                    justifyContent="center" 
                                    alignItems="center"
                                >
                                    <Grid item>
                                        <TextField id="registration-name" 
                                            label="Season Name" 
                                            variant="standard" 
                                            value={seasonName} 
                                            onChange={handleChangeSeasonName}
                                            required
                                            sx={{
                                                width:"100%", 
                                                minWidth: "270px", 
                                                marginTop: "1rem"
                                            }}
                                        />
                                    </Grid>
                                    <Grid item> 
                                        <LocalizationProvider dateAdapter={AdapterDateFns}>
                                            <DatePicker
                                                openTo="month"
                                                views={['month', 'day']}
                                                label="Select when the season starts"
                                                value={startDate}
                                                onChange={(newValue) => {
                                                    setStartDate(newValue);
                                                }}
                                                renderInput={(params) => 
                                                    <TextField {...params} 
                                                        helperText={null} 
                                                        sx={{
                                                            width:"100%", 
                                                            minWidth: "270px",
                                                            marginBottom: "0.5rem", 
                                                            marginTop: "0.5rem"
                                                        }}
                                                    />
                                                }
                                            />
                                        </LocalizationProvider>
                                    </Grid>
                                    <Grid item> 
                                        <LocalizationProvider dateAdapter={AdapterDateFns}>
                                            <DatePicker
                                                openTo="month"
                                                views={['month', 'day']}
                                                label="Select when the season Ends"
                                                value={endDate}
                                                onChange={(newValue) => {
                                                    setEndDate(newValue);
                                                }}
                                                renderInput={(params) => 
                                                    <TextField {...params} 
                                                        helperText={null} 
                                                        sx={{width:"100%", minWidth: "270px"}}
                                                    />
                                                }
                                            />
                                        </LocalizationProvider>
                                    </Grid>
                                    <Grid item>
                                    <TextField id="registration-description" 
                                        label="Description" 
                                        type="text"
                                        variant="standard" 
                                        value={description}
                                        onChange={handleChangeDescription}
                                        multiline = {true}
                                        rows={4}
                                        required
                                        placeholder='250 Charecters max'
                                        inputProps={{ maxLength: 250 }}
                                        sx={{
                                            width:"100%", 
                                            minWidth: "270px", 
                                            marginTop: "1rem"
                                        }}
                                    />
                                    </Grid>
                                </Grid>
                            </Box>
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={submitForm}>Add</Button>
                            <Button onClick={cancelSeason}>Cancel</Button>
                        </DialogActions>
                    </form>
                </Dialog>

            <Card sx={{ maxWidth: 345 }}>
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {SEASON_NAME}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" sx={{height: "10rem", overflow: "hidden", textOverflow: "string"}}>
                        {SEASON_DESCRIPTION}
                    </Typography>
                    <Typography variant='subtitle1' sx={{marginTop: "0.5rem"}}>
                        Duration: {`${SEASON_START_DAY} ${months[SEASON_START_MONTH]} - ${SEASON_END_DAY} ${months[SEASON_END_MONTH]}`}
                    </Typography>
                </CardContent>
                <CardActions>
                    <Button 
                        size="small" 
                        onClick={handleClickEdit}
                    >
                        Edit
                    </Button>
                    <Button 
                        size="small" 
                        color="error" 
                        // onClick={handleClickOpenDelete} 
                        >
                        Delete
                    </Button>
                </CardActions>
            </Card>
        </React.Fragment>
    )
}
