import React, {useState, useEffect} from 'react';

import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';

import style from "../../styles/seasonsStyles"

function Season(){
    const classes = style();
    const [openAddForm, setOpenAddForm] = useState(false);

    const openForm = () => {
        setOpenAddForm(true);
    }

    return(
        <div>
            <Box sx={{ width: '100%' }}>
                <Typography variant="h3" component="div" gutterBottom className={classes.heading}>
                    Add a new season
                </Typography>
                {openAddForm === false ? (
                    <Button variant="contained" 
                        className={classes.placeButton}
                        onClick={openForm}
                        disabled={openAddForm}
                    >
                            Add a season
                    </Button>
                    ) : (
                        <div></div>
                    )
                }

            </Box>
        </div>
    )
}

export default Season;

<form >

<Box sx={{ width: '100%' }}>
    <Grid container item direction="column" spacing={2} xs={12}>
        <Grid item>
            <TextField id="registration-name" 
                label="Name" 
                variant="standard" 
                value={name} 
                onChange={handleChangeName}
                required
                fullWidth
            />
        </Grid>

    
        <Grid item>
            <Typography variant="subtitle1">
                Already have an account?
            </Typography>
            <Button fullWidth variant="contained" ><Link to="/login">Login</Link></Button>
        </Grid>
    </Grid>
</Box>
</form>
