import axios from "axios";

export const login = async (email, password) => {
    try {
        const formdata = new FormData();
        formdata.append("email", email);
        formdata.append("password", password);
        return await axios.post('http://localhost:5000/api/user/login', formdata);
    } 
    catch (err) {
        return -1;
    }
}

export const register = async (name, businessName, password, address, phoneNo, email) => {
    try {
        const formdata = new FormData();
        formdata.append("name", name);
        formdata.append("businessName", businessName);
        formdata.append("password", password);
        formdata.append("address", address);
        formdata.append("phoneNo", phoneNo);
        formdata.append("email", email);
        return await axios.post('http://localhost:5000/api/user/registration', formdata);
    }
    catch (err) {
        return -1;
    }
}

export const logout = async () => {
    try {
        return await axios.post('http://localhost:5000/api/user/logout');
    }
    catch(err){
        return -1;
    }
}
