import axios from "axios";
import { authAxios } from "../helper";
const API_URL = 'http://localhost:5000'

export const fetchProducts = async () => {
    try {
        return await axios.get(`${API_URL}/api/products`);
    }
    catch(err){
        return -1;
    }
}

export const productDetails = async (id) => {
    try {
        return await axios.get(`${API_URL}/api/products/${id}`);
    }
    catch(err){
        return -1;
    }
}

export const addToCart = async (productId, quantity) => {
    try {
        const formdata = new FormData();
        formdata.append("userId", localStorage.getItem("userId"));
        formdata.append("productId", productId);
        formdata.append("quantity", quantity);

        return await authAxios.post(`${API_URL}/api/products/cart`, formdata);
    }
    catch (err){
        console.log(err)
        return -1;
    }
}
