import React, {useState, useEffect} from 'react';

import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';

import style from "../../styles/seasonsStyles"

function Season(){
    const classes = style();
    const [openAddForm, setOpenAddForm] = useState(0);

    const openForm = () => {
        setOpenAddForm(1);
    }

    return(
        <div>
            <Box sx={{ width: '100%' }}>
                <Typography variant="h3" component="div" gutterBottom className={classes.heading}>
                    Add a new season
                </Typography>
                {}
                <Button variant="contained" 
                    className={classes.placeButton}
                    onClick={openForm}
                    // disabled={}
                >
                        Add a season
                </Button>
            </Box>
        </div>
    )
}

export default Season;
