import React, {useState} from 'react'

// MaterialUI Elements
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import DatePicker from '@mui/lab/DatePicker';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';

import * as adminApi from "../../../api/admin"
import useInputState from "../../../hooks/useInputState"

export default function SeasonsItems(props) {
    const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

    const {
        SEASON_DESCRIPTION,
        SEASON_END_DAY,
        SEASON_END_MONTH,
        SEASON_NAME,
        SEASON_START_DAY,
        SEASON_START_MONTH,
        SEASON_ID
    } = props

    console.log(props)

    let startMonth = SEASON_START_MONTH;
    let startDay = SEASON_START_DAY;
    let endMonth = SEASON_END_MONTH;
    let endDay = SEASON_END_DAY;

    if(SEASON_START_MONTH < 9){
        startMonth = `0${SEASON_START_MONTH+1}`
    }
    if(SEASON_START_DAY < 9){
        startDay = `0${SEASON_START_DAY}`
    }
    if(SEASON_END_MONTH < 9){
        endMonth = `0${SEASON_END_MONTH+1}`
    }
    if(SEASON_END_DAY < 9){
        endDay = `0${SEASON_END_DAY}`
    }

    const startDateString = `2022-${startMonth}-${startDay}T00:00:00.000`
    const startEndString = `2022-${endMonth}-${endDay}T00:00:00.000`

    const [openEditForm, setOpenEditForm] = useState(false);
    const [seasonName, handleChangeSeasonName] = useInputState(SEASON_NAME);
    const [description, handleChangeDescription] = useInputState(SEASON_DESCRIPTION);
    const [startDate, setStartDate] = useState(new Date(startDateString));
    const [endDate, setEndDate] = useState(new Date(startEndString));
    const [snakeBarOpen, setSnakeBarOpen] = useState(false);
    const [snakeBarType, setSnakeBarType] = useState("success");
    const [snakeMessage, setSnakeMessage] = useState("");


    const cancelSeasonEdit = () => {
        setOpenEditForm(false);
    }

    const handleClickEdit = () => {
        setOpenEditForm(true);
    }

    const updateForm = async () => {
        const res = await adminApi.updateSeasons(seasonName, startDate.getDay(), startDate.getMonth(), endDate.getDay(), endDate.getMonth(), description, SEASON_ID)
        if(res.status === 200){
            setSnakeBarOpen(true);
            setSnakeBarType("success");
            setSnakeMessage("Successfully updated");
            window.location.reload()
        }
        else {
            setSnakeBarOpen(true);
            setSnakeBarType("error");
            setSnakeMessage("Failed to Update");
        }
        setOpenEditForm(false);
    }


    return (
        <React.Fragment>

            <Dialog open={openEditForm} onClose={cancelSeasonEdit}>
                    <form>
                        <DialogTitle>Add Season</DialogTitle>
                        <DialogContent>
                            <DialogContentText>
                                To add a new season, enter the name of the season and select the start and end date of the season. year dosen't matter, select the month and date.
                            </DialogContentText>
                            <Box sx={{ width: '100%' }}>
                                <Grid container 
                                    item 
                                    direction="column" 
                                    spacing={2} 
                                    justifyContent="center" 
                                    alignItems="center"
                                >
                                    <Grid item>
                                        <TextField id="registration-name" 
                                            label="Season Name" 
                                            variant="standard" 
                                            value={seasonName} 
                                            onChange={handleChangeSeasonName}
                                            required
                                            sx={{
                                                width:"100%", 
                                                minWidth: "270px", 
                                                marginTop: "1rem"
                                            }}
                                        />
                                    </Grid>
                                    <Grid item> 
                                        <LocalizationProvider dateAdapter={AdapterDateFns}>
                                            <DatePicker
                                                openTo="month"
                                                views={['month', 'day']}
                                                label="Select when the season starts"
                                                value={startDate}
                                                onChange={(newValue) => {
                                                    setStartDate(newValue);
                                                }}
                                                renderInput={(params) => 
                                                    <TextField {...params} 
                                                        helperText={null} 
                                                        sx={{
                                                            width:"100%", 
                                                            minWidth: "270px",
                                                            marginBottom: "0.5rem", 
                                                            marginTop: "0.5rem"
                                                        }}
                                                    />
                                                }
                                            />
                                        </LocalizationProvider>
                                    </Grid>
                                    <Grid item> 
                                        <LocalizationProvider dateAdapter={AdapterDateFns}>
                                            <DatePicker
                                                openTo="month"
                                                views={['month', 'day']}
                                                label="Select when the season Ends"
                                                value={endDate}
                                                onChange={(newValue) => {
                                                    setEndDate(newValue);
                                                }}
                                                renderInput={(params) => 
                                                    <TextField {...params} 
                                                        helperText={null} 
                                                        sx={{width:"100%", minWidth: "270px"}}
                                                    />
                                                }
                                            />
                                        </LocalizationProvider>
                                    </Grid>
                                    <Grid item>
                                    <TextField id="registration-description" 
                                        label="Description" 
                                        type="text"
                                        variant="standard" 
                                        value={description}
                                        onChange={handleChangeDescription}
                                        multiline = {true}
                                        rows={6}
                                        required
                                        fullWidth
                                        placeholder='250 Charecters max'
                                        inputProps={{ maxLength: 250 }}
                                        sx={{
                                            width:"100%", 
                                            minWidth: "270px", 
                                            marginTop: "1rem"
                                        }}
                                    />
                                    </Grid>
                                </Grid>
                            </Box>
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={updateForm}>Add</Button>
                            <Button onClick={cancelSeasonEdit}>Cancel</Button>
                        </DialogActions>
                    </form>
                </Dialog>

            <Card sx={{ maxWidth: 345 }}>
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {SEASON_NAME}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" sx={{height: "10rem", overflow: "hidden", textOverflow: "string"}}>
                        {SEASON_DESCRIPTION}
                    </Typography>
                    <Typography variant='subtitle1' sx={{marginTop: "0.5rem"}}>
                        Duration: {`${SEASON_START_DAY} ${months[SEASON_START_MONTH]} - ${SEASON_END_DAY} ${months[SEASON_END_MONTH]}`}
                    </Typography>
                </CardContent>
                <CardActions>
                    <Button 
                        size="small" 
                        onClick={handleClickEdit}
                    >
                        Edit
                    </Button>
                    <Button 
                        size="small" 
                        color="error" 
                        // onClick={handleClickOpenDelete} 
                        >
                        Delete
                    </Button>
                </CardActions>
            </Card>
        </React.Fragment>
    )
}
