import React, {useState, useEffect} from 'react';

import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Grid from '@mui/material/Grid';

import style from "../../styles/seasonsStyles"
import useInputState from '../../hooks/useInputState';

function Season(){
    const classes = style();
    const [openAddForm, setOpenAddForm] = useState(false);
    const [seasonName, handleChangeSeasonName, setSeasonName] = useInputState("");

    const openForm = () => {
        setOpenAddForm(true);
    }

    const submitForm = (e) => {
        e.preventDefault();
    }

    return(
        <div>
            <Box sx={{ width: '100%' }}>
                <Typography variant="h3" component="div" gutterBottom className={classes.heading}>
                    Add a new season
                </Typography>
                {openAddForm === false ? (
                    <Button variant="contained" 
                        className={classes.placeButton}
                        onClick={openForm}
                        disabled={openAddForm}
                    >
                            Add a season
                    </Button>
                    ) : (
                        <form>
                            <Box sx={{ width: '100%' }}>
                                <Grid container item direction="column" spacing={2} xs={12} md={8} lg={6}>
                                    <Grid item>
                                        <TextField id="registration-name" 
                                            label="Season Name" 
                                            variant="standard" 
                                            value={seasonName} 
                                            onChange={handleChangeSeasonName}
                                            required
                                            fullWidth
                                        />
                                    </Grid>
                                    <Grid item>
                                        <Button fullWidth onClick={submitForm} variant="contained" >Registration</Button>
                                    </Grid>
                                </Grid>
                            </Box>
                        </form>
                    )
                }

            </Box>
        </div>
    )
}

export default Season;
